//Fake Users
const users = [
    {
        id: '1',
        name: 'John Smith'
    },
    {
        id: '2',
        name: 'James Doe'
    },
    {
        id: '3',
        name: 'Porgues D Ace'
    } 
];

       const resolvers = {
    Query: {
        me: (parent, args, context, info) => me,
        user: (parent, args, context, info) => {
            const id = args.id;
            const user = users.find( user => user.id === id);

            return user;
        },
        users: (parent, args, context, info) => users
    }
}

       const typeDefs = gql`
    type Query {
        me: User!
        user(id: ID!): User
        users: [User]!
    }

    type User {
        id: ID!
        name: String!
    }
`;  